-- Create users table
CREATE TABLE users (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    name TEXT NOT NULL,
    height INTEGER NOT NULL, -- in cm
    start_weight DECIMAL(5,2) NOT NULL,
    target_weight DECIMAL(5,2) NOT NULL,
    age INTEGER NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE users ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can view their own profile" ON users FOR SELECT USING (auth.uid() = id);
CREATE POLICY "Users can update their own profile" ON users FOR UPDATE USING (auth.uid() = id);
CREATE POLICY "Users can insert their own profile" ON users FOR INSERT WITH CHECK (auth.uid() = id);

-- Create daily_entries table
CREATE TABLE daily_entries (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
    date DATE NOT NULL,
    weight DECIMAL(5,2),
    total_points INTEGER DEFAULT 0,
    mood TEXT CHECK (mood IN ('gut', 'okay', 'schlecht')),
    notes TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
    UNIQUE(user_id, date)
);

-- Enable RLS
ALTER TABLE daily_entries ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can view their own daily entries" ON daily_entries FOR SELECT USING (auth.uid() = user_id);
CREATE POLICY "Users can insert their own daily entries" ON daily_entries FOR INSERT WITH CHECK (auth.uid() = user_id);
CREATE POLICY "Users can update their own daily entries" ON daily_entries FOR UPDATE USING (auth.uid() = user_id);
CREATE POLICY "Users can delete their own daily entries" ON daily_entries FOR DELETE USING (auth.uid() = user_id);

-- Create activities table
CREATE TABLE activities (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    daily_entry_id UUID NOT NULL REFERENCES daily_entries(id) ON DELETE CASCADE,
    activity_type TEXT NOT NULL CHECK (activity_type IN ('wiegen', 'sport', 'wasser', 'gesunde_mahlzeit')),
    points_earned INTEGER NOT NULL,
    completed_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE activities ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can view their own activities" ON activities 
FOR SELECT USING (
    daily_entry_id IN (
        SELECT id FROM daily_entries WHERE user_id = auth.uid()
    )
);

CREATE POLICY "Users can insert their own activities" ON activities 
FOR INSERT WITH CHECK (
    daily_entry_id IN (
        SELECT id FROM daily_entries WHERE user_id = auth.uid()
    )
);

CREATE POLICY "Users can update their own activities" ON activities 
FOR UPDATE USING (
    daily_entry_id IN (
        SELECT id FROM daily_entries WHERE user_id = auth.uid()
    )
);

CREATE POLICY "Users can delete their own activities" ON activities 
FOR DELETE USING (
    daily_entry_id IN (
        SELECT id FROM daily_entries WHERE user_id = auth.uid()
    )
);

-- Create progress_photos table
CREATE TABLE progress_photos (
    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
    photo_url TEXT NOT NULL,
    measurements JSONB, -- for Bauchumfang, Hüftumfang etc.
    taken_at DATE NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE progress_photos ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can view their own progress photos" ON progress_photos FOR SELECT USING (auth.uid() = user_id);
CREATE POLICY "Users can insert their own progress photos" ON progress_photos FOR INSERT WITH CHECK (auth.uid() = user_id);
CREATE POLICY "Users can update their own progress photos" ON progress_photos FOR UPDATE USING (auth.uid() = user_id);
CREATE POLICY "Users can delete their own progress photos" ON progress_photos FOR DELETE USING (auth.uid() = user_id);
